// 산술 연산자에 대한 예제
public class Operator01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num01 = 10, num02 = 5;
		
		System.out.printf("%d + %d = %d%n",num01,num02,num01+num02);
		System.out.printf("%d - %d = %d%n",num01,num02,num01-num02);
		System.out.printf("%d * %d = %d%n",num01,num02,num01*num02);
		System.out.printf("%d / %d = %d%n",num01,num02,num01/num02);
		System.out.printf("%d %% %d = %d%n",num01,num02,(num01%num02));
	}

}
